var clock='';var nums=60;layui.use("layer",function(){});function getPhoneCode(){let graphValidateCode=$("#graphValidateCode").val();let countryCode=$("#countryCode").val();if(!graphValidateCode){layer.alert("请输入图形验证码！");return}let tel=$("#phoneNo").val();if(!tel){layer.alert("请输入手机号！");return}if(!/^1\d{10}$/.test(tel)){layer.alert('请输入正确的手机号');return}if(!checkPhoneNo(tel)){return}$.ajax({url:"/admin/enterprise_sendVerificationCode",data:{user_Name:tel,countryCodeValue:countryCode,graphValidateCode:graphValidateCode},method:'POST',success:function(data){if(data.code=="0"){$("#getSmsCodeBtn").addClass('layui-disabled').html(nums+'秒后重获');clock=setInterval(countDown,1000);layer.msg("验证码发送成功，请在手机上查看")}else{layer.msg(data.msg);change()}},error:function(data){layer.msg("系统繁忙，请稍后再试")}})}function countDown(){nums--;if(nums>0){$("#getSmsCodeBtn").addClass('layui-disabled').html(nums+'秒后重获');$("#getSmsCodeBtn").prop('disabled',true)}else{$("#getSmsCodeBtn").removeClass('layui-disabled').html('获取验证码');$("#getSmsCodeBtn").prop('disabled',false);clearInterval(clock);nums=60;change()}}function submitForm(){let verifyCode=$("#verifyCode").val();if(!verifyCode){layer.alert("请输入验证码！");return}let tel=$("#phoneNo").val();if(!tel){layer.alert("请输入手机号！");return}let enterpriseName=$("#enterpriseName").val();if(!enterpriseName){layer.alert("请输入企业名称！");return}let password=$("#loginPassword").val();if(!password){layer.alert("请输入密码！");return}let repass=$("#loginRepassword").val();if(!repass){layer.alert("请再次输入密码！");return}if(repass!=password){layer.alert("两次密码输入不一致！");return}$.ajax({url:"/admin/enterprise_registerEnterpriseUser",data:$("#layuiForm").serialize(),method:'POST',success:function(res){if(res.code=='0'){layer.alert('注册成功',{icon:1,time:2000},function(){window.parent.location.href="/"});return}else{layer.alert(res.msg);return false}},error:function(res){layer.alert("注册失败")}})}function change(){document.getElementById("imgsrc").setAttribute("src","/Kaptcha.do?"+Math.floor(Math.random()*100))}function checkPhoneNo(phoneNo){$.ajax({url:"/admin/enterprise_checkPhoneRepeat",data:{user_Name:phoneNo},method:'POST',success:function(data){if(data.code=="-1"){layer.alert("该手机号已注册！",{offset:['180px']});return false}},error:function(data){layer.alert("系统繁忙，请稍后再试");return false}});return true}